import React, { createContext, FC } from 'react';
import {ItemContextType} from './type';


export const ITEM_DEFAULT:ItemContextType = {
    item: {},
    loading: false,
    error: false,
};

export const ItemContext = createContext(ITEM_DEFAULT);

export const ItemProvider:FC = ({children}) => {
    // const [reminder, setReminder] = useState({});
    // const [isLoading, setLoading] = useState(false);
    // const [isErr, setErr] = useState(false);

    return (
        <ItemContext.Provider value={{item:{}, loading:false, error:false}}>
            {children}
        </ItemContext.Provider>
    );
}
