export const itemReducer = (state, action) => {
    switch (action.type) {
        case 'SET':
          return action.items;
        case 'ADD':
          return [...state, action.item];
        case 'DELETE':
          return state.filter(reminderItem => reminderItem.ItemId !== action.id);
        default:
          throw new Error('Should not get there!');
    }

}
